import { Component } from '@angular/core';

@Component({
  selector: 'app-ui',
  templateUrl: './ui.component.html',
  styleUrls: ['./ui.component.css']
})
export class UiComponent {

  lengthOptions = [
    {
      id: 0,
      label: 'Kilometre',
      unit: 'km'
    },
    {
      id: 1,
      label: 'Metre',
      unit: 'm'
    },
    {
      id: 2,
      label: 'Centimetre',
      unit: 'cm'
    }
  ];

  inp: number = 0;
  resultinp: number = 0;
  op1: string = '';
  op2: string = '';

  convert(value: string) 
  {
    if (value === 'inp') 
    {
      if (this.op1 === 'Kilometer' && this.op2 === 'Meter') 
      {
        this.resultinp = this.inp * 1000;
      } 

      else if (this.op1 === 'Kilometer' && this.op2 === 'Kilometer') 
      {
        this.resultinp = this.inp;
      }

      else if (this.op1 === 'Kilometer' && this.op2 === 'Centimeter') 
      {
        this.resultinp = this.inp*100000;
      }

      else if (this.op1 === 'Meter' && this.op2 === 'Kilometer') 
      {
        this.resultinp = this.inp/1000;
      }
      else if (this.op1 === 'Meter' && this.op2 === 'Meter') 
      {
        this.resultinp = this.inp;
      }


    }





    else if (value === 'resultinp') 
    {
      if (this.op2 === 'Kilometer' && this.op1 === 'Meter') 
      {
        this.inp = this.resultinp / 1000;
      } 


      else if (this.op2 === 'Kilometer' && this.op1 === 'Kilometer') 
      {
        this.inp = this.resultinp;
      }
      else if (this.op2 === 'kilometer' && this.op1 === 'Centimeter') 
      {
        this.inp = this.resultinp/100000;
      }


      else if (this.op2 === 'Meter' && this.op1 === 'Kilometer') 
      {
        this.inp = this.resultinp * 1000;
      }

      else if (this.op2 === 'Meter' && this.op1 === 'Meter') 
      {
        this.inp = this.resultinp;
      }

    }

  }
}
<div class="layout-row align-items-center justify-content-center mt-50">
    <section class="layout-column align-items-center justify-content-between">
        <span class="input layout-row">
            <input type="number" class="large" [(ngModel)]="inp" (input)="convert('inp')" data-test-id="app-input1" />
        </span>
        <br>
        <select [(ngModel)]="op1" (change)="convert('op1')" data-test-id="app-select1">
            <option value="Kilometer">Kilometer</option>
            <option value="Meter">Meter</option>
            <option value="Centimeter">Centimeter</option>
        </select>
    </section>

    <label>⇋</label>

    <section class="layout-column align-items-center justify-content-between">
        <span class="input layout-row">
            <input type="number" class="large" [(ngModel)]="resultinp" (input)="convert('resultinp')" data-test-id="app-input2" />
        </span>
        <br>
        <select [(ngModel)]="op2" (change)="convert('op2')" data-test-id="app-select2">
            <option value="Kilometer">Kilometer</option>
            <option value="Meter">Meter</option>
            <option value="Centimeter">Centimeter</option>
        </select>
    </section>
</div>
