import { Component } from '@angular/core';

@Component({
  selector: 'app-ui',
  templateUrl: './ui.component.html',
  styleUrls: ['./ui.component.css']
})
export class UiComponent {

  lengthOptions = [
    {
      id: 0,
      label: 'Kilometre',
      unit: 'km'
    },
    {
      id: 1,
      label: 'Metre',
      unit: 'm'
    },
    {
      id: 2,
      label: 'Centimetre',
      unit: 'cm'
    }
  ];

  inp1:number=0;
  inp2:number=0;
  selectfrom:string='';
  selectedto:string='';


  result()
  {
      let   input= this.selectfrom;
      let   res=this.selectedto; 
     if(input==='kilometer' && res==='metre')
     {
         return this.inp1*0.001;
     }
     else
     {
      return this.inp1;
     }
  }

}
